insert into t_bond_info(bond_info_ID,task_Info_No,bond_Name,bond_Group_Name,bond_Group_Type,bond_End_Date,balance) select T_BOND_INFO_SEQUENCE.nextval as bond_info_ID,A.* from (
select #{item.taskInfoNo,jdbcType=BIGINT} as task_Info_No, #{item.bondName,jdbcType=VARCHAR} as bond_Name, #{item.bondGroupName,jdbcType=VARCHAR} as bond_Group_Name, #{item.bondGroupType,jdbcType=VARCHAR} as bond_Group_Type, #{item.bondEndDate,jdbcType=VARCHAR} as bond_End_Date, #{item.balance,jdbcType=VARCHAR} as balance from dual
)A
